sub flip_button {
    my ($self, $id, $state) = @_;
    $state = !$self->{"tx_btw_xs_$id"} unless defined $state;
    $self->{_check_changed}++ unless $self->{"tx_btw_xs_$id"} == $state;
    $self->{"tx_btw_xs_$id"} = $state;
    $self->{"tx_btw_xx_${id}"}->SetBitmapLabel($state ? $bm_edit_remove : $bm_edit_trash);
}

